<?php

declare(strict_types=1);

namespace tonkatsutei\automatic_eye_catch\no_image;

use tonkatsutei\automatic_eye_catch\eye_catch\_eye_catch;
use tonkatsutei\automatic_eye_catch\base\_options;

if (!defined('ABSPATH')) exit;
class _no_image
{
	public static function set_no_image(int $post_id): void
	{
		// no imageが登録されていなければ登録
		$img_id = (int)_options::get('no_imd_id');
		if ($img_id === 0) {
			if (!function_exists('media_handle_upload')) {
				require_once(ABSPATH . 'wp-admin/includes/image.php');
				require_once(ABSPATH . 'wp-admin/includes/file.php');
				require_once(ABSPATH . 'wp-admin/includes/media.php');
			}

			// 保存
			$upload_dir = wp_upload_dir();
			$file_name = "no_image_" . md5(uniqid()) . ".png";
			//$file_url  = $upload_dir['url']  . '/' . $file_name;
			$temp_path = $upload_dir['path'] . '/' . $file_name;
			$img_data = base64_decode(self::$no_image);
			file_put_contents($temp_path, $img_data);

			// メディアに登録
			$f = [
				'name' => $file_name,
				'tmp_name' => $temp_path,
			];
			$img_id = media_handle_sideload($f);
			_options::update('no_image_id', serialize($img_id));
		}

		// no imageをアイキャッチに設定
		set_post_thumbnail($post_id, $img_id);
	}

	public static function add_no_image(): void
	{
	}

	// NO IMAGE
	public static $no_image = "";
}
